INSERT INTO public.card VALUES (157, '02-139_third_squadron', 'Third Squadron', 'Dark Prophecies', 139, 'U', 'Troop', '{Dragon}', '{"Band of the Red Hand"}', 'This troop gains +1 Combat for every 3 Band of the Red Hand troops you control.  All [Opposition] generated by this troop may be converted into [Support].', 'A troop of foot behind a mounted officer in a dented breastplate and Cairhienin helmet marched… close to two hundred pikemen, weapons a tall forest of spikes...', 0, 0, 0, 0, 0, 0, 3, 1);
