INSERT INTO public.card VALUES (228, '02-084_narrow_pass', 'Narrow Pass', 'Dark Prophecies', 84, 'C', 'Event', '{""}', '{Terrain}', 'Target a card-based challenge.  Only one terrain card may target each challenge.  Each player may only participate in the target challenge with a maximum of 2 characters and 2 troops.', 'Under the looming Spine of the World, Rand guided Jeade''en up the stony slope from the foothills that began the foot of the Jangai Pass.', 0, 0, 0, 0, 0, 0, 0, 0);
