INSERT INTO public.card VALUES (248, '02-041_heart_of_the_stone', 'Heart of the Stone', 'Dark Prophecies', 41, 'C', 'Advantage', '{Player}', '{""}', 'All High Lords and High Ladies you control additionally generate [Support] [Opposition] when they roll their dice.  If you control the Callandor character advantage, all challenges which target you or a card you control gain 5 additional opposition.', 'The anteroom was a round chamber fifty paces or more across.  A hundred gilded lamps hung on golden chains from its high ceiling.  Polished redstone columns made an inner ring, and the floor appeared to be one huge slab of black marble…', 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO public.card VALUES (258, '02-059_lead_them_to_ruin', 'Lead Them to Ruin', 'Dark Prophecies', 59, 'C', 'Challenge', '{""}', '{""}', 'Target a player.  For each 2 by which this challenge succeeds, the target must return to his hand a player advantage he controls (of his choice) with a printed cost.', 'She glanced over her shoulder toward the Tower, the thick white shaft dominating the city, whole and straight, yet broken as surely as if it lay in ruins.', 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO public.card VALUES (268, '02-086_on_all_sides', 'On All Sides', 'Dark Prophecies', 86, 'C', 'Event', '{Limited}', '{""}', 'Discard from your hand every copy of every non-Multiple character card which is currently in play.  For each card discarded, add the ability symbols listed in the card''s printed cost to your pool.  These symbols have all allegiances.', 'That act may send half the armies of the south marching to the Borderlands, but the evidence is that they''re willing to take that risk.', 0, 0, 0, 0, 0, 0, 0, 0);
