INSERT INTO public.card VALUES (148, '03-136_the_traveling_people', 'The Traveling People', 'Children of the Dragon', 136, 'C', 'Event', '{""}', '{""}', 'Play during the Coming to Grips phase of a target challenge.  Remove 1 point of damage from all non-Monster characters and troops you control participating in the target challenge who have damage on them greater than or equal to their highest printed ability.', 'A wiry man, gray-haired and short, stepped forward and bowed gravely, both hands pressed against his chest.', 0, 0, 0, 0, 0, 0, 0, 0);
