INSERT INTO public.card VALUES (178, '02-038_graendal_(ii)', 'Graendal (II)', 'Dark Prophecies', 38, 'R1', 'Character', '{"Dark One"}', '{Forsaken}', 'Only ability symbols generated by a Forsaken may be used to recruit this character.  Symbols generated by this character may not be used to recruit.  When Graendal rotates to heal, you may reveal any number of cards in your hand with the name Graendal.  For each card revealed, remove an additional point of damage from Graendal.  As an event, discard from your hand to remove up to 2 damage from your starting character.', 'They won''t speak out of turn.  They worship me.  Don''t you?', 3, 0, 3, 0, 4, 4, 0, 0);
