INSERT INTO public.card VALUES (321, '01-015_animate_stone', 'Animate Stone', 'Premiere', 15, 'U', 'Event', '{""}', '{""}', 'Target a challenge during its Opening Moves step.  This card becomes a character, participating in that challenge, with one Combat ability for each [One Power Icon] you pay.  Roll its dice immediately add the results to your pool.  Discard this card after the challenge resolves.', 'Suddenly the benches shifted, flowed, changed into faceless manshapes, still as white and hard-looking as the stone.', 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO public.card VALUES (331, '01-026_bodyguards', 'Bodyguards', 'Premiere', 26, 'F', 'Advantage', '{Character}', '{""}', 'When the targeted character takes damage, apply it to this card instead.  Discard this advantage when it has taken two points of damage and apply any extra damage to the targeted character.', 'Facing every direction as they did, they were supposed to keep all intruders from the current lord of the Stone.', 1, 0, 0, 0, 0, 0, 1, 0);
INSERT INTO public.card VALUES (341, '01-036_caracarn', 'Car''a''carn', 'Premiere', 36, 'U', 'Challenge', '{""}', '{""}', 'Only the Dragon Reborn may initiate this challenge.  If you succeed, this challenge becomes a character advantage on the Dragon Reborn giving him the Aiel allegiance.  For each point by which you succeed, place a follower token on the advantage.  You may remove one follower token to reduce the cost of an Aiel character or troop by one ability of your choice.', 'Of the blood, but not raised by the blood, he will come from Rhuidean at dawn, and tie you together with bonds you cannot break.', 0, 0, 0, 0, 0, 0, 0, 0);
