INSERT INTO public.card VALUES (359, '01-056_decisive_tactics', 'Decisive Tactics', 'Premiere', 56, 'F', 'Event', '{""}', '{""}', 'Play this card during the Opening Moves or Coming to Grips step of a challenge.  For each [Combat Icon x2] you pay, apply one [Sword Icon] or [Shield Icon] to the challenge.', 'Slowly, inexorably, the Two Rivers line bulged inward at a dozen places.  If it broke, anywhere...', 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO public.card VALUES (369, '01-064_dragonsworn', 'Dragonsworn', 'Premiere', 64, 'R', 'Troop', '{Dragon}', '{Multiple}', 'There is no cost to bring the Dragonsworn into play.', 'They''re down there, on Almoth Plain, and the Light alone knows where else.  Hundreds of them.  Thousands.  They declared for the Dragon Reborn because I raised that banner.', 0, 0, 0, 0, 0, 0, 2, 0);
INSERT INTO public.card VALUES (379, '01-074_establish_contacts', 'Establish Contacts', 'Premiere', 74, 'U', 'Advantage', '{Character}', '{""}', 'Target a Forsaken character.  He gains +1 Intrigue.', 'This world is very changed from the world I... went to sleep in... What I know of what lies where, I have learned since waking.', 0, 0, 0, 0, 0, 0, 0, 0);
