INSERT INTO public.card VALUES (399, '01-094_gateway', 'Gateway', 'Premiere', 94, 'U', 'Event', '{Limited}', '{""}', 'Target one character or troop per [One Power Icon x2] you pay.  Each target is readied, moved to the battleground and gains Reinforcement (until the end of the turn).', 'Seizing the source, Rand made a gateway between the posts, four paces by four, and ran through as he tied off the weave... into a great open square surrounded by huge white columns, each topped with a marble wreath of olive branches.', 0, 0, 0, 0, 0, 0, 0, 0);
