INSERT INTO public.card VALUES (311, '01-006_a_time_of_decision', 'A Time of Decision', 'Premiere', 6, 'C', 'Challenge', '{""}', '{""}', 'Target a character you have in play.  If you succeed, retrieve a replacement version of the character from your deck and place it in your hand.', 'All he wanted was to be a blacksmith, to go home, and see his family again, and work at the smithy.  But it was not to be; he knew that.', 0, 0, 0, 0, 0, 0, 0, 0);
