INSERT INTO public.card VALUES (362, '01-057_defensive_measures', 'Defensive Measures', 'Premiere', 57, 'C', 'Event', '{""}', '{""}', 'Target one or more ready troops you control that are participating in the same challenge.  Pay [Combat Icon x2] for each target.  For each [Shield Icon] the roll, they generate an additional [Shield].', 'The road passed a half a dozen farms... ending at the heavy wooden gates bound with wide straps of black iron.  They were closed tight, even if the sun was not down yet.', 0, 0, 0, 0, 0, 0, 0, 0);
